mapper.insert:
INSERT INTO member_tbl(
member_id
, member_pwd
, member_name
, member_gender
, member_phone
, member_birthday
, member_prefer
, member_zipcode
, member_address
, member_reg_date
, member_up_date
) VALUES (
#{member_id}
, #{member_pwd}
, #{member_name}
, #{member_gender}
, #{member_phone}
, #{member_birthday}
, #{member_prefer}
, #{member_zipcode}
, #{member_address}
, now()
, now()
)

mapper.update:
update member_tbl set 
	member_name = #{member_name}
	, member_gender = #{member_gender}
	, member_zipcode = #{member_zipcode}
	, member_address = #{member_address}
	, member_phone = #{member_phone}
	, member_birthday = #{member_birthday}
	, member_prefer = #{member_prefer}
	, member_up_date = now()
where member_id = #{member_id}

UPDATE member_tbl 
SET member_borrowCnt = member_borrowCnt + 1
WHERE member_id = #{member_id}

UPDATE member_tbl 
SET member_reservationCnt = member_reservationCnt + 1
WHERE member_id = #{member_id}

UPDATE member_tbl 
SET member_interestedCnt = member_interestedCnt + 1
WHERE member_id = #{member_id}
