mapper.insert:
INSERT INTO staff_tbl(
staff_id
, staff_pwd
, staff_name
, staff_gender
, staff_phone
, staff_birthday
, staff_level
,staff_department
,staff_reg_date
,staff_up_date
) VALUES (
#{staff_id}
, #{staff_pwd}
, #{staff_name}
, #{staff_gender}
, #{staff_phone}
, #{staff_birthday}
, #{staff_level}
, #{staff_department}
, now()
, now()
)

mapper.update:
update staff_tbl set 
	staff_name = #{staff_name}
	, staff_gender = #{staff_gender}
	, staff_phone = #{staff_phone}
	, staff_birthday = #{staff_birthday}
	, staff_level = #{staff_level}
	, staff_department = #{staff_department}
	, staff_up_date = now()
where staff_id = #{staff_id}
